import React, {Component, useEffect, useState, useRef} from 'react';
import styled from 'styled-components';
import icon from '../../assets/drop.svg';
import '../css/card.css';

const Options = [
    {id: '1', value: 'select'},
    {id: '2', value: 'Pass'},
    {id: '3', value: 'Fail'},
    {id: '4', value: 'Rest'},
    {id: '5', value: 'Late'}
];

const useDetectClose = (initialState) => {
    const [isOpen, setIsOpen] = useState(initialState);
    const ref = useRef(null);

    const removeHandler = () => {
        setIsOpen(!isOpen);
    }

    useEffect(() => {
        const onClick = (e) => {
            if(ref.current != null && !ref.current.contains(e.target)) {
                setIsOpen(!isOpen);
            }
        };

        if(isOpen) {
            window.addEventListener("click", onClick);
        }

        return () => {
            window.removeEventListener("click", onClick);
        };
    }, [isOpen]);

    return [isOpen, ref, removeHandler];
};

export const Dropdown = () => {
    const [isOpen, ref, handler] = useDetectClose(['select']);

    return (
        <Container>
            <Select onClick={handler} ref={ref}>
                <text>{isOpen}</text>
                <img src={icon}></img>
            </Select>
            <Menu></Menu>
        </Container>
    )
}

const primary = '#394B3F';
const lprimary ='#91A096';
const background = '#FFFCEB';

const Container = styled.div`
    position: relative;
    display: block;
    width: 100%;
    height: 100%;
`;

const Select = styled.button`
    display: flex;
    flex-direction: row;
    width: 100%;
    height: 100%;
    padding: 0px 13px 0px 4px;
    justify-content: space-between;
    align-items: center;

    background-color: rgba(0,0,0,0);
    color: ${primary};
    font: 14px ITC Avant Garde Gothic Pro, sans-serif;
    font-weight: 300;
    line-height: 22px;
`;

const Menu = styled.div`
    position: absolute;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    width: 100%;
    height: 104px;
    background-color: ${lprimary};
    z-index: -1;
`;
