import React, {Component} from 'react';
import styled from 'styled-components';
import '../css/card.css';
import data from '../../data/data.json';

function Day() {
    const basicStyle = {
        display: "block",
        width: "28px",
        height: "28px",
        borderRadius: "50%",

        fontFamily: 'itc-avant-grade-gothic, sans-serif',
        fontStyle: "normal",
        fontWeight: 300,
        fontSize: "14px",

        textAlign: "center",
        lineHeight: "28px",
    }
    const dayStyles = [
        {color: `${primaryColor}`,
        opacity: "60%",},
        {width: "26px",
        height: "26px",
        lineHeight: "26px",
        border: `${primaryColor} 1px solid`,
        color: `${primaryColor}`,},
        {backgroundColor: `${primaryColor}`,
        color: `${backgroundColor}`,},
        {backgroundColor: `${secondaryColor}`,
        color: `${backgroundColor}`},
        {backgroundColor: `${lprimaryColor}`,
        color: `${backgroundColor}`,},
        {width: "26px",
        height: "26px",
        lineHeight: "26px",
        border: `${secondaryColor} 1px solid`,
        color: `${secondaryColor}`,},
    ];
    return(
        <div style={Object.assign({}, basicStyle, dayStyles[this.props.check])}>{this.props.day}</div>
    );
}

class Week extends Component {
    constructor(props){
        super(props);
    }

    render(){
        const days = ['M', 'T', 'W', 'T', 'F', 'S', 'S'];
        const weekStyle = {
            display: "flex",
            flexDirection: "row",
            justifyContent: "space-between",
        }
        return (
            <div style={weekStyle}>
                {
                    //0: Future, 1: Today, 2: Pass, 3: Fail, 4: Rest, 5: Late
                    Object.entries(days).map(([key, value]) => {
                        return(
                            <Day day={value} check={data['호두']['check'][key]}/>
                        )
                    })
                }
            </div>
        );
    }
}
class Card extends Component {
    constructor(props){
        super(props);
        this.state={
            name: '호두',
            time: '9:15',
            remains: [],
            money: [],
        }
    }

    render (){
        return (
            <Container>
                <Profile>
                    <Pic src={require("../../data/hodu.jpg")}/>
                    <Kor>{this.state.name}</Kor>
                    <Line></Line>
                </Profile>
                <Check>
                    <Week name={this.state.name}></Week>
                    <Time>
                        <Eng>Time</Eng>
                        <Eng>{this.state.time}</Eng>
                    </Time>
                    <Line></Line>
                </Check>
                <Options>
                    <Line></Line>
                </Options>
                <Remain>
                    <Line></Line>
                </Remain>
                <Status>
                    <Line></Line>
                </Status>
            </Container>
        )
    }
}

const primaryColor = "#394B3F";
const lprimaryColor = "#91A096";
const secondaryColor = "#FABB01";
const backgroundColor = "#FFFCEB";

const Eng = styled.text`
    color: ${primaryColor};
    font: 14px ITC Avant Garde Gothic Pro, sans-serif;
    font-weight: 300;
    line-height: 22px;
`;
const Kor = styled.text`
    color: ${primaryColor};
    font: 16px Pretendard, sans-serif;
    font-weight: 400;
    line-height: 24px;
`;
const Line = styled.hr`
    width: 100%;
    height: 2px;
    border: none;
    background-color: ${primaryColor};

    margin: 0;
    position: absolute;
    bottom: 0%;
`;

const Container = styled.div`
    display: grid;
    grid-template-rows: 150px 101px 60px 112px 1fr;
    height: 558px;
    width: 212px;
`;

const Profile = styled.div`
    position: relative;
    margin: 0;
    padding: 0;
    display: grid;
    justify-items: center;
`;

const Pic = styled.img`
    width: 110px;
    height: 110px;
    border: 2px solid ${primaryColor};
`;

const Check = styled.div`
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
`;

const Time = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    padding: 0px 4px 0px 4px;
`;

const Options = styled.div`
    position: relative;
    display: block;
`;

const Remain = styled.div`
    position: relative;
    display: flex;
`;

const Status = styled.div`
    position: relative;
    display: flex;
`;

export default Card;
