import React, {Component} from 'react';
import styled from 'styled-components';
import '../css/card.css';

class Day extends Component{
    constructor(props){
        super(props);
        //0: 미래, 1: Pass, 2: Fail, 3: Rest
        this.state = {
            check: 3,
        }
    }
    render(){
        const basicStyle = {
            display: "block",
            width: "28px",
            height: "28px",
            borderRadius: "50%",

            fontFamily: 'itc-avant-grade-gothic, sans-serif',
            fontStyle: "normal",
            fontWeight: 300,
            fontSize: "14px",

            textAlign: "center",
            lineHeight: "28px",
        }
        const dayStyles = [
            {color: `${primaryColor}`,
            opacity: "60%",},
            {backgroundColor: `${primaryColor}`,
            color: `${backgroundColor}`,},
            {backgroundColor: `${secondaryColor}`,
            color: `${backgroundColor}`},
            {backgroundColor: `rgba(${primaryColor}, 0.6)`,
            color: `${backgroundColor}`,}
        ];
        return(
            <div style={Object.assign({}, basicStyle, dayStyles[this.state.check])}>{this.props.day}</div>
        );
    }
}
function Week() {
    const days = ['M', 'T', 'W', 'T', 'F', 'S', 'S'];
    const weekStyle = {
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between",
    }
    return (
        <div style={weekStyle}>
            {
                days.map(day => <Day day={day}/>)
            }  
        </div>
    );
}
class Card extends Component {
    render (){
        return (
            <Container>
                <Profile></Profile>
                <Check>
                    <Week></Week>
                </Check>
                <Options></Options>
                <Remain></Remain>
                <Status></Status>
            </Container>
        )
    }
}

const primaryColor = "#394B3F";
const secondaryColor = "#FABB01";
const backgroundColor = "#FFFCEB";

const Container = styled.div`
    display: grid;
    grid-template-rows: 150px 101px 60px 112px 1fr;
    height: 558px;
    width: 212px;
`;

const Profile = styled.div`
    display: grid;
    background-color: #222;
`;

const Check = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
`;

const Options = styled.div`
    display: block;
    background-color: #666;
`;

const Remain = styled.div`
    display: flex;
    background-color: #888;
`;

const Status = styled.div`
    display: flex;
    background-color: #AAA;
`;

export default Card;
