import React, {Component, useState} from 'react';
import styled from 'styled-components';
import '../css/card.css';

class Day extends Component{
  constructor(props){
        super(props);
        this.state = {
            check: null
        }
    }
    render(){
        const dayStyle = {
            display: "block",
            width: "28px",
            height: "28px",
            borderRadius: "50%",

            backgroundColor: `${primaryColor}`,
            color: `${backgroundColor}`,

            fontFamily: '&quot;itc-avant-grade-gothic',
            fontStyle: "normal",
            fontWeight: 300,
            fontSize: "14px",

            textAlign: "center",
        }
        return(
            <div style={dayStyle}>{this.props.day}</div>
        );
    }
}
function Week() {
    const days = ['M', 'T', 'W', 'T', 'F', 'S', 'S'];
    return (
        days.map(day => <Day day={day}/>)
    );
}
class Card extends Component {
    render (){
        return (
            <Container>
                <Profile></Profile>
                <Check>
                    <Week></Week>
                </Check>
                <Options></Options>
                <Remain></Remain>
                <Status></Status>
            </Container>
        )
    }
}

const primaryColor = "#394B3F";
const secondaryColor = "#FABB01";
const backgroundColor = "#FFFCEB";

const Container = styled.div`
    display: grid;
    grid-template-rows: 150px 101px 60px 112px 1fr;
    height: 558px;
    width: 212px;
`;

const Profile = styled.div`
    display: grid;
    background-color: #222;
`;

const Check = styled.div`
    display: flex;
`;

const Options = styled.div`
    display: block;
    background-color: #666;
`;

const Remain = styled.div`
    display: flex;
    background-color: #888;
`;

const Status = styled.div`
    display: flex;
    background-color: #AAA;
`;

export default Card;
