import React, {Component} from 'react';
import styled from 'styled-components';
import '../css/card.css';

class Day extends Component{
    render(){
        const basicStyle = {
            display: "block",
            width: "28px",
            height: "28px",
            borderRadius: "50%",

            fontFamily: 'itc-avant-grade-gothic, sans-serif',
            fontStyle: "normal",
            fontWeight: 300,
            fontSize: "14px",

            textAlign: "center",
            lineHeight: "28px",
        }
        const dayStyles = [
            {color: `${primaryColor}`,
            opacity: "60%",},
            {width: "26px",
            height: "26px",
            lineHeight: "26px",
            border: `${primaryColor} 1px solid`,
            color: `${primaryColor}`,},
            {backgroundColor: `${primaryColor}`,
            color: `${backgroundColor}`,},
            {backgroundColor: `${secondaryColor}`,
            color: `${backgroundColor}`},
            {backgroundColor: `${lprimaryColor}`,
            color: `${backgroundColor}`,},
            {width: "26px",
            height: "26px",
            lineHeight: "26px",
            border: `${secondaryColor} 1px solid`,
            color: `${secondaryColor}`,},
        ];
        return(
            <div style={Object.assign({}, basicStyle, dayStyles[this.props.check])}>{this.props.day}</div>
        );
    }
}
function Week() {
    const days = ['M', 'T', 'W', 'T', 'F', 'S', 'S'];
    const weekStyle = {
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between",
    }
    return (
        <div style={weekStyle}>
            {
                //0: Future, 1: Today, 2: Pass, 3: Fail, 4: Rest, 5: Late
                days.map(day => <Day day={day} check={1}/>)
            }
        </div>
    );
}
class Card extends Component {
    constructor(props){
        super(props);
        this.state={
            name: null,
            time: null,
            remains: [],
            money: [],
        }
    }

    render (){
        return (
            <Container>
                <Profile>{this.state.name}</Profile>
                <Check>
                    <Week></Week>
                    <Time>
                        <text>Time:</text>
                        <text></text>
                    </Time>
                </Check>
                <Options></Options>
                <Remain></Remain>
                <Status></Status>
            </Container>
        )
    }
}

const primaryColor = "#394B3F";
const lprimaryColor = "#91A096";
const secondaryColor = "#FABB01";
const backgroundColor = "#FFFCEB";

const Container = styled.div`
    display: grid;
    grid-template-rows: 150px 101px 60px 112px 1fr;
    height: 558px;
    width: 212px;
`;

const Profile = styled.div`
    display: grid;
    background-color: #222;
`;

const Check = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
`;

const Time = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
`

const Options = styled.div`
    display: block;
    background-color: #666;
`;

const Remain = styled.div`
    display: flex;
    background-color: #888;
`;

const Status = styled.div`
    display: flex;
    background-color: #AAA;
`;

export default Card;
