import React, {Component, useState} from 'react';
import styled from 'styled-components';
import '../css/card.css';

class Day extends Component{
  constructor(props){
        super(props);
        this.state = {
            check: null
        }
    }
    render(){
        return(
            <div style="display: block;
            width: 28px;
            height: 28px;
            border-radius: 50%;

            background-color: ${primaryColor};
            color: ${backgroundColor};

            font-family: 'ITC Avant Garde Gothic Pro';
            font-style: normal;
            font-weight: 300;
            font-size: 14px;

            text-align: center;">{this.props.day}</div>
        );
    }
}
function Week() {
    const days = ['M', 'T', 'W', 'T', 'F', 'S', 'S'];
    return (
        days.map(day => <Day day={day}/>)
    );
}
class Card extends Component {
    render (){
        return (
            <Container>
                <Profile></Profile>
                <Check>
                    <Week></Week>
                </Check>
                <Options></Options>
                <Remain></Remain>
                <Status></Status>
            </Container>
        )
    }
}

const primaryColor = "#394B3F";
const secondaryColor = "#FABB01";
const backgroundColor = "#FFFCEB";

const Container = styled.div`
    display: grid;
    grid-template-rows: 150px 101px 60px 112px 1fr;
    height: 558px;
    width: 212px;
`;

const Profile = styled.div`
    display: grid;
    background-color: #222;
`;

const Check = styled.div`
    display: flex;
`;

const Options = styled.div`
    display: block;
    background-color: #666;
`;

const Remain = styled.div`
    display: flex;
    background-color: #888;
`;

const Status = styled.div`
    display: flex;
    background-color: #AAA;
`;

export default Card;
